SELECT ma.* , mu.avatar, mu.user_id, mu.nickname, ma2.content parent_content, SUM( CASE WHEN mas.like_status = 1 THEN 1 ELSE 0 END ) AS like_sum, SUM( CASE WHEN mas.dislike_status = 1 THEN 1 ELSE 0 END ) AS dislike_sum, SUM( CASE WHEN mas.star_status = 1 THEN 1 ELSE 0 END ) AS star_sum, SUM( CASE WHEN mas.repost_status = 1 THEN 1 ELSE 0 END ) AS repost_sum, mas2.like_status, mas2.dislike_status, mas2.repost_status, mas2.star_status FROM mt_article ma LEFT JOIN mt_article ma2 ON ma2.article_id = ma.parent_id LEFT JOIN mt_article_status mas ON mas.article_id = ma.article_id LEFT JOIN mt_article_status mas2 ON mas2.article_id = ma.article_id AND mas2.user_id = #{userId} LEFT JOIN mt_user mu ON mu.user_id = ma.user_id WHERE ma.is_deleted = ${NOT_DELETED} AND ma.limit_id <> #{private}
AND ma.user_id IN
#{id}
AND 1 = 0
AND ma.user_id NOT IN
#{blockId}
GROUP BY ma.article_id ORDER BY ma.gmt_create DESC LIMIT #{start}, #{end}

SELECT ma.* , mu.avatar, mu.user_id, mu.nickname, ma2.content parent_content, SUM( CASE WHEN mas.like_status = 1 THEN 1 ELSE 0 END ) AS like_sum, SUM( CASE WHEN mas.dislike_status = 1 THEN 1 ELSE 0 END ) AS dislike_sum, SUM( CASE WHEN mas.star_status = 1 THEN 1 ELSE 0 END ) AS star_sum, SUM( CASE WHEN mas.repost_status = 1 THEN 1 ELSE 0 END ) AS repost_sum, mas2.like_status, mas2.dislike_status, mas2.repost_status, mas2.star_status FROM mt_article ma LEFT JOIN mt_article ma2 ON ma2.article_id = ma.parent_id LEFT JOIN mt_article_status mas ON mas.article_id = ma.article_id LEFT JOIN mt_article_status mas2 ON mas2.article_id = ma.article_id AND mas2.user_id = #{userId} LEFT JOIN mt_user mu ON mu.user_id = ma.user_id WHERE ma.is_deleted = ${NOT_DELETED}
AND ma.user_id NOT IN
#{blockId}
AND ( ma.limit_id = #{public}
OR ( ma.limit_id = #{follow} AND ma.user_id IN
#{followId}
)
OR (ma.user_id = #{userId}) ) GROUP BY ma.article_id ORDER BY ma.gmt_create DESC LIMIT #{start}, #{end}

SELECT ma.* , mu.avatar, mu.user_id, mu.nickname, ma2.content parent_content, SUM( CASE WHEN mas.like_status = 1 THEN 1 ELSE 0 END ) AS like_sum, SUM( CASE WHEN mas.dislike_status = 1 THEN 1 ELSE 0 END ) AS dislike_sum, SUM( CASE WHEN mas.star_status = 1 THEN 1 ELSE 0 END ) AS star_sum, SUM( CASE WHEN mas.repost_status = 1 THEN 1 ELSE 0 END ) AS repost_sum, mas2.like_status, mas2.dislike_status, mas2.repost_status, mas2.star_status FROM mt_article ma LEFT JOIN mt_article ma2 ON ma2.article_id = ma.parent_id LEFT JOIN mt_article_status mas ON mas.article_id = ma.article_id LEFT JOIN mt_article_status mas2 ON mas2.article_id = ma.article_id AND mas2.user_id = #{userId} LEFT JOIN mt_user mu ON mu.user_id = ma.user_id WHERE ma.is_deleted = ${NOT_DELETED}
AND ma.user_id NOT IN
#{blockId}
AND ( ma.limit_id = #{public}
OR ( ma.limit_id = #{follow} AND ma.user_id IN #{followId}
) OR (ma.user_id = #{userId})
) GROUP BY ma.article_id ORDER BY like_sum DESC LIMIT #{start}, #{end}
