mapper.select:

SELECT
*
FROM
mt_relation_limit
WHERE
is_deleted = ${NOT_DELETED}
AND `relation_status` IN (
SELECT
`status`
FROM
mt_user_relation
WHERE
( SELECT `status` FROM `mt_user_relation` WHERE src_id = #{destId} AND dest_id = #{srcId} )  <>  #{block}
AND src_id = #{srcId}
AND dest_id = #{destId}
)
